mapper.select:

select 

from oc_product_attribute
where product_id = #{productId,jdbcType=INTEGER}
  and attribute_id = #{attributeId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}

mapper.delete:

delete from oc_product_attribute
where product_id = #{productId,jdbcType=INTEGER}
  and attribute_id = #{attributeId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}

mapper.update:

update oc_product_attribute


    `text` = #{text,jdbcType=LONGVARCHAR},


where product_id = #{productId,jdbcType=INTEGER}
  and attribute_id = #{attributeId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}


update oc_product_attribute
set `text` = #{text,jdbcType=LONGVARCHAR}
where product_id = #{productId,jdbcType=INTEGER}
  and attribute_id = #{attributeId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}
