mapper.select:

select 

from oc_category_description
where category_id = #{categoryId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}

mapper.delete:

delete from oc_category_description
where category_id = #{categoryId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}

mapper.update:

update oc_category_description
set `name` = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=LONGVARCHAR},
  meta_title = #{metaTitle,jdbcType=VARCHAR},
  meta_description = #{metaDescription,jdbcType=VARCHAR},
  meta_keyword = #{metaKeyword,jdbcType=VARCHAR}
where category_id = #{categoryId,jdbcType=INTEGER}
  and language_id = #{languageId,jdbcType=INTEGER}

mapper.select:
